package com.arquitecturajava;

public class HolaMundo8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<10; i=i+1) {
			System.out.println(i);
		}
		int j=0;
		while (j<10) {
			System.out.println(j);
			j=j+1;
		}
		int k =0;
		do {
			System.out.println(k);
			k++;
		} while (k<10);
	}

}
